sources.main.scss:
@import "./mixins.scss";

* {
    box-sizing: border-box;
}

.calendly-badge-widget {
    opacity: 0 !important;
    pointer-events: none !important;
}

body{
    padding: 0;
    margin: 0;
    font-size: 24px; // 1rem
}
.mt-10{
    padding-top: 10px !important;
}
.mt-20{
    padding-top: 20px !important;
}
.mt-30{
    padding-top: 30px !important;
}
.header{
    position: fixed;
    top: 20px;
    right: 20px;
    left: 20px;
    width: calc(100% - 40px);
    background-color: #fff;
    z-index: 100;
    height: 60px;
    border-radius: 16px;
    overflow: hidden;
    box-shadow: 0px 4px 6px 0px rgba(0,0,0,0.25);
    @include media ("<=tablet") {
        top: 0;
        left: 0;
        right: 0;
        width: 100%;
        border-radius: 0px;
        overflow: visible;
    }
}
.main-nav{
    width: 100%;
    height: 100%;
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
    padding-left: 16px;
    position: relative;
}
.main-nav__link{
    color: inherit;
    text-decoration: none;
}
.main-nav__logo{
    display: flex;
    align-items: center;
    &--img{
        width: 29px;
        height: 32px;
    }
    &--text{
        font-family: 'Kumbh Sans', sans-serif;
        font-size: 20px;
        line-height: 1.2;
        font-style: normal;
        font-weight: bold;
        text-align: left;
        color: #1F155B;
        margin: 0px 0px 0px 12px;
    }
}
.main-nav__menu{
    width: calc( 100% - 350px);
    display: flex;
    flex-direction: row;
    margin-right: 170px;
    align-items: center;
    max-width: 850px;
    @include media ("<=tablet"){ 
        display: none;
        background-color: #fff;
        height: calc(100% - 60px);
        top: 60px;
        left: 0;
        right: 0;
        width: 100%;
        z-index: 10;
        max-width: unset;
        flex-direction: column;
        justify-content: space-between;
        position: fixed;
    }
    &--open{
        display: flex;
    }
}
.main-nav__list{
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
    width: 100%;
    position: relative;
    // padding-bottom: 25px;
    @include media ("<=tablet"){ 
        margin: 0;
        height: 70%;
        flex-direction: column;
        justify-content: space-around;
        margin-top: 10%;
    }

}
.main-nav__list-item{
    font-family: 'Kumbh Sans', sans-serif;
    font-size: 16px;
    line-height: 1.2;
    font-style: normal;
    font-weight: bold;
    text-align: left;
    color: #1F155B;
    cursor: pointer;
    display: inline;
    transition: color 0.3s ease;
    margin-right: 20px;
    &:hover{
        color: #4730D2;
    }
    a{
        color: inherit;
        text-decoration: none;
    }
    @include media ("<=tablet"){
        display: block;
        font-size: 32px;
        text-align: center;
        margin-right: 0;
    }
    &.active {
        color: #4730D2;
    }
}
.main-nav__list-sub{
    display: flex;
    flex-direction: row;
    align-items: center;
    a{
        font-family: 'Kumbh Sans', sans-serif;
        font-size: 16px;
        line-height: 1.2;
        font-style: normal;
        font-weight: bold;
        text-align: left;
        color: #1F155B;
        cursor: pointer;
        display: inline;
        text-align: center;
        margin-right: 20px;
        transition: color 0.3s ease;
        text-decoration: none;
        &:hover{
            color: #4730D2;
        }
        @include media ("<=tablet"){
            display: block;
            font-size: 24px;
            text-align: center;
            margin-right: 0;
        }
    }
    &.active {
        a{ color: #4730D2; }
    }

}
.main-nav__demo-btn{
    position: absolute;
    right: 0;
    top: 0;
    left: auto;
    display: block;
    height: 60px;
    font-family: 'Kumbh Sans', sans-serif;
    line-height: 1.1;
    font-style: normal;
    font-weight: bold;
    text-align: left;
    color: #fff;
    text-decoration: none;
    align-self: flex-end;
    // padding: 0px 24px 0px 24px;
    // background-color: #47CE7D;
    @include media ("<=tablet"){
        text-align: center;
        position: relative;
        // height: 75px;
        width: 100%;
        font-size: 32px;
    }
    &--undefined{
        font-size: 20px;
        background-color: #4730D2;
        padding: 21px 24px 19px 24px;
        display: block;
        height: 60px;
        color: inherit;
        text-decoration: none;
        cursor: pointer;
    }
    &--defined{
        background-color: #15B743;
        font-size: 17px;
        padding: 23px 24px 19px 24px;
        display: block;
        height: 60px;
        pointer-events: none;
        color: #fff;
        text-decoration: none;
        text-align: center;
        cursor: unset;
    }
}

// Menu burger
.main-nav__burger {
    display: none;
    position: absolute;
    top: 50%;
    right: 0px;
    width: 60px;
    height: 60px;
    transform: translate(0, -50%);
    transition: 0.5s ease-in-out;
    cursor: pointer;
    z-index: 1500;
    background-color: #4730D2;
    @include media ("<=tablet") {
        display: block;
    }
    > span {
      display: block;
      position: absolute;
      height: 2px;
      width: 40px;
      background: #fff;
      border-radius: 2px;
      opacity: 1;
      left: 10px;
      top: 0;
      transform: rotate(0deg);
      transition: 0.25s ease-in-out;

      &:nth-child(1) {
        top: 19px;
      }

      &:nth-child(2) {
        top: 29px;
      }

      &:nth-child(3) {
        top: 39px;
      }
    }

    &.open {
        background-color: #fff;
        > span {
            background-color: #4730D2;
            &:nth-child(1) {
                top: 30px;
                transform: rotate(135deg);
            }

            &:nth-child(2) {
                opacity: 0;
                right: -100%;
            }

            &:nth-child(3) {
                top: 30px;
                transform: rotate(-135deg);
            }
        }
    }
}



