@import "./mixins.scss";

* {
    box-sizing: border-box;
}

.calendly-badge-widget {
    opacity: 0 !important;
    pointer-events: none !important;
}

body{
    padding: 0;
    margin: 0;
    font-size: 24px; // 1rem
}
.mt-10{
    padding-top: 10px !important;
}
.mt-20{
    padding-top: 20px !important;
}
.mt-30{
    padding-top: 30px !important;
}
.header{
    position: fixed;
    top: 20px;
    right: 20px;
    left: 20px;
    width: calc(100% - 40px);
    background-color: #fff;
    z-index: 100;
    height: 60px;
    border-radius: 16px;
    overflow: hidden;
    box-shadow: 0px 4px 6px 0px rgba(0,0,0,0.25);
    @include media ("<=tablet") {
        top: 0;
        left: 0;
        right: 0;
        width: 100%;
        border-radius: 0px;
    }
}
.main-nav{
    width: 100%;
    height: 100%;
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
    padding-left: 16px;
}
.main-nav__link{
    color: inherit;
    text-decoration: none;
}
.main-nav__logo{
    display: flex;
    align-items: center;
    &--img{
        width: 29px;
        height: 32px;
    }
    &--text{
        font-family: 'Kumbh Sans', sans-serif;
        font-size: 20px;
        line-height: 1.2;
        font-style: normal;
        font-weight: bold;
        text-align: left;
        color: #1F155B;
        margin: 0px 0px 0px 12px;
    }
}
.main-nav__menu{
    width: calc( 100% - 350px);
    display: flex;
    flex-direction: row;
    margin-right: 170px;
    align-items: center;
    max-width: 850px;
    @include media ("<=tablet"){ 
        display: none;
        background-color: #fff;
        height: calc(100vh - 60px);
        top: 60px;
        left: 0;
        right: 0;
        width: 100%;
        z-index: 10;
        max-width: unset;
        flex-direction: column;
        justify-content: space-between;
        position: fixed;
    }
    &--open{
        display: flex;
    }
}
.main-nav__list{
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
    width: 100%;
    position: relative;
    // padding-bottom: 25px;
    @include media ("<=tablet"){ 
        margin: 0;
        height: 70%;
        flex-direction: column;
        justify-content: space-around;
        margin-top: 10%;
    }

}
.main-nav__list-item{
    font-family: 'Kumbh Sans', sans-serif;
    font-size: 16px;
    line-height: 1.2;
    font-style: normal;
    font-weight: bold;
    text-align: left;
    color: #1F155B;
    cursor: pointer;
    display: inline;
    transition: color 0.3s ease;
    margin-right: 20px;
    &:hover{
        color: #4730D2;
    }
    a{
        color: inherit;
        text-decoration: none;
    }
    @include media ("<=tablet"){
        display: block;
        font-size: 32px;
        text-align: center;
        margin-right: 0;
    }
    &.active {
        color: #4730D2;
    }
}
.main-nav__list-sub{
    display: flex;
    flex-direction: row;
    align-items: center;
    a{
        font-family: 'Kumbh Sans', sans-serif;
        font-size: 16px;
        line-height: 1.2;
        font-style: normal;
        font-weight: bold;
        text-align: left;
        color: #1F155B;
        cursor: pointer;
        display: inline;
        text-align: center;
        margin-right: 20px;
        transition: color 0.3s ease;
        text-decoration: none;
        &:hover{
            color: #4730D2;
        }
        @include media ("<=tablet"){
            display: block;
            font-size: 24px;
            text-align: center;
            margin-right: 0;
        }
    }
    &.active {
        a{ color: #4730D2; }
    }

}
.main-nav__demo-btn{
    position: absolute;
    right: 0;
    top: 0;
    left: auto;
    display: block;
    height: 60px;
    font-family: 'Kumbh Sans', sans-serif;
    line-height: 1.1;
    font-style: normal;
    font-weight: bold;
    text-align: left;
    color: #fff;
    text-decoration: none;
    align-self: flex-end;
    // padding: 0px 24px 0px 24px;
    // background-color: #47CE7D;
    @include media ("<=tablet"){
        text-align: center;
        position: relative;
        // height: 75px;
        width: 100%;
        font-size: 32px;
    }
    &--undefined{
        font-size: 20px;
        background-color: #4730D2;
        padding: 21px 24px 19px 24px;
        display: block;
        height: 60px;
        color: inherit;
        text-decoration: none;
        cursor: pointer;
    }
    &--defined{
        background-color: #15B743;
        font-size: 17px;
        padding: 23px 24px 19px 24px;
        display: block;
        height: 60px;
        pointer-events: none;
        text-align: center;
        cursor: unset;
    }
}

// Menu burger
.main-nav__burger {
    display: none;
    position: absolute;
    top: 50%;
    right: 0px;
    width: 60px;
    height: 60px;
    transform: translate(0, -50%);
    transition: 0.5s ease-in-out;
    cursor: pointer;
    z-index: 1500;
    background-color: #4730D2;
    @include media ("<=tablet") {
        display: block;
    }
    > span {
      display: block;
      position: absolute;
      height: 2px;
      width: 40px;
      background: #fff;
      border-radius: 2px;
      opacity: 1;
      left: 10px;
      top: 0;
      transform: rotate(0deg);
      transition: 0.25s ease-in-out;

      &:nth-child(1) {
        top: 19px;
      }

      &:nth-child(2) {
        top: 29px;
      }

      &:nth-child(3) {
        top: 39px;
      }
    }

    &.open {
        background-color: #fff;
        > span {
            background-color: #4730D2;
            &:nth-child(1) {
                top: 30px;
                transform: rotate(135deg);
            }

            &:nth-child(2) {
                opacity: 0;
                right: -100%;
            }

            &:nth-child(3) {
                top: 30px;
                transform: rotate(-135deg);
            }
        }
    }
}



.page{
    width: 100%;
}

.hero{
    width: 100%;
    height: calc(100vh + 20px);
    position: relative;
    max-height: 845px;
    background-image: url('./assets/img/hero-bg.jpg');
    background-position: center right;
    background-size: cover;
    background-repeat: no-repeat;
    @include media ("<=tablet"){
        max-height: unset;
     }
    @include media ("<=smallTablet"){
        background-position: 80% 0;
    }
}
.overlay-black{
    display: none;
    @include media ("<phone"){
       background-color: rgba(#000, 0.25);
       position: absolute;
       top: 0;
       left: 0;
       right: 0;
       width: 100%;
       height: 100%;
       display: block;
    }
}
.hero__title{
    width: 80%;
    max-width: 760px;
    height: auto;
    margin: 0 auto;
    position: absolute;
    left: 0;
    right: 0;
    top: 57%;
    transform: translate(0, -50%);
    @include media ("<phone"){
        left: 20px;
        right: 20px;
        width: calc(75% - 40px);
    }
}
.hero__title-text{
    font-family: 'Kumbh Sans', sans-serif;
    line-height: 1.17;
    font-style: normal;
    text-align: center;
    color: #fff;
    margin-top: 0;
    width: 100%;
    display: inline-block;
    &--h1{
        font-size: 52px; 
        font-weight: bold;
        text-shadow: 0px 4px 12px rgba(31, 21, 91, 0.2);
        @include media ("<phone"){
            font-size: 36px;
        }
    }
    &--h3{
        font-size: 24px;
        font-weight: 400;
        letter-spacing: 1px;
        line-height: 1.2;
        width: 70%;
        margin-left: 15%;
        @include media ("<phone"){
            font-size: 20px;
            width: 100%;
            margin-left: 0;
        }
    }
}

.section{
    width: 100%;
    &--1{
        margin-top: -20px;
    }
    &--2{
        padding-top: 230px;
        .gradient{
            height: calc(100% + 40px);
        }
        .section__main-sub{
            padding-top: 12px;
            padding-bottom: 1px;
        }
        .section__title{
            padding-top: 10px;
        }
        @include media ("<phone"){
            padding-top: 10px;
        }
    }
    &--3{
        padding-top: 226px;
        padding-bottom: 120px;
    }
    &--4{
        padding-bottom: 180px;
        .gradient{
            height: calc(100% + 40px);
        }
        .section__main-sub{
            padding-top: 15px;
            padding-bottom: 0px;
            background-color: #1F155B;
        }
    }
    &--5{
        padding-top: 140px;
        padding-bottom: 148px;
    }
    &--6{
        .gradient{
            height: calc(100% + 20px);
        }
        .section__main-sub{
            max-width: 1160px;
            padding-top: 40px;
            padding-bottom: 100px;
            margin: 0 auto;
            border-radius: 48px;
            box-shadow: 0px 7px 12px 0px rgba(0,0,0,0.1);
            @include media ("<=tablet"){
                max-width: 980px;
            }
            @include media ("<=smallTablet"){
                border-radius: 0px;
                margin: 0;
                max-width: 100%;
             }
        }
        .section__title{
            font-size: 48px;
            margin-top: 30px;
        }
    }
}
.content_width{
    max-width: 1080px;
    width: 100%;
    margin: 0 auto;
    z-index: 4;
    position: relative;
    &--big{
        max-width: 1161px;
        padding: 1px;
    }
    @include media ("<=tablet"){
        max-width: 920px;
        &--big{
            max-width: 980px;
        }
    }
    @include media ("<=smallTablet"){
        width: calc(100% - 80px);
        max-width: 728px;
    }
    @include media ("<phone"){
        width: calc(100% - 40px);
        max-width: 380px;
    }
}
.section__main-sub{
    padding-top: 100px;
    padding-bottom: 110px;
    position: relative;
    margin-top: 20px;
    background-color: #4730D2;
}
.gradient{
    position: absolute;
    z-index: 0;
    display: block;
    width: 100%;
    height: 100%;
    overflow: hidden;
    top: -20px;
    left: 0;
    right: 0;
}
.gradient__img{
    position: absolute;
    top: 0%;
    left: 0;
    right: 0;
    width: 100%;
    height: auto;
    display: block;
    min-height: 400px;
    &--1{
        transform: translate(0, -50%);
    }
    &--2{
        right: -50%;
        left: auto;
        transform: translate(0, -50%);
    }
    &--3{
        bottom: 0;
        top: auto;
        left: -30%;
        right: auto;
        transform: translate(0, 50%);
    }
    &--4{
        top: auto;
        bottom: 0%;
        transform: translate(0, 0%);
    }
    &--5{
        top: auto;
        bottom: 0;
        transform: translate(0, 60%);
    }
}
.section__title{
    font-family: 'Kumbh Sans', sans-serif;
    font-style: normal;
    text-align: center;
    color: #fff;
    font-size: 96px;
    line-height: 1.1;
    font-weight: bold;
    max-width: 956px;
    width: 100%;
    margin: 0 auto;
    &--blue{
        color: #1F155B;
    }
    @include media ("<=smallTablet"){
        font-size: 68px;
    }
    @include media ("<phone"){
        font-size: 46px;
    }
}
.section__sub-title{
    font-family: 'Kumbh Sans', sans-serif;
    font-style: normal;
    text-align: center;
    color: #fff;
    font-size: 24px;
    line-height: 1.25;
    font-weight: 400;
    max-width: 400px;
    width: 100%;
    margin: 0 auto;
    margin-top: 22px;
    &--grey{
        color: #6F6C82;
        max-width: 640px;
    }

}
.section__pre-title{
    font-family: 'Kumbh Sans', sans-serif;
    font-style: normal;
    text-align: center;
    color: rgba(255, 255, 255, 0.48);
    font-size: 16px;
    line-height: 1.2;
    font-weight: 400;
    text-transform: uppercase;
    letter-spacing: 1px;
    margin: 0;
    padding-top: 38px;
    @include media ("<=smallTablet"){
        max-width: 220px;
        margin: 0 auto;
    }
}
.sub__list{
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    margin-top: 100px;
    @include media ("<=smallTablet"){
        flex-direction: column;
    }
}
.sub__list-item{
    @include media ("<=smallTablet"){
        margin-bottom: 50px;
    }
}
.sub__list-icon{
    position: relative;
    height: 180px;
    margin: 0 auto;
    &--1{
        width: 208px;
    }
    &--2{
        width: 142px;
    }
    &--3{
        width: 136px;
    }
}
.sub__list-icon--img{
    position: absolute;
    bottom: 0;
    left: 0px;
    right: 0px;
    &--1{
        width: 192px;
        height: 172px;
        left: 18px;
        @include media ("<=smallTablet"){
            left: 0;
        }
    }
    &--2{
        bottom: 22px;
        left: 57px;
        width: 192px;
        height: 172px;
        @include media ("<=smallTablet"){
            left: 40px;
        }
    }
    &--3{
        width: 165px;
        height: 165px;
        @include media ("<=smallTablet"){
            width: 185px;
            left: -30px;
            height: auto;
        }
    }
    &--4{
        bottom: 25px;
        left: 22px;
        width: 165px;
        height: 165px;
        @include media ("<=smallTablet"){
            width: 185px;
            left: 12px;
            height: auto;
        }
    }
    &--5{
        left: -5px;
        width: 160px;
        height: 160px;
        @include media ("<=smallTablet"){
            left: -35px;
            width: 185px;
            height: auto;
        }
    }
    &--6{
        bottom: 25px;
        left: 20px;
        width: 160px;
        height: 160px;
        @include media ("<=smallTablet"){
            left: 25px;
            width: 185px;
            height: auto;
            left: 10px;
        }
    }
}
.sub__list-title{
    font-family: 'Kumbh Sans', sans-serif;
    font-style: normal;
    text-align: center;
    color: #fff;
    font-size: 32px;
    line-height: 1.2;
    font-weight: bold;
    margin: 5px 0;
    @include media ("<=smallTablet"){
        font-size: 32px;
    }
}
.sub__list-text{
    font-family: 'Kumbh Sans', sans-serif;
    font-style: normal;
    text-align: center;
    color:rgba(255, 255, 255, 0.48);
    font-size: 24px;
    line-height: 1.3;
    font-weight: 400;
    max-width: 270px;
    margin: 0 auto;
    display: block;
    @include media ("<=smallTablet"){
        max-width: 100%;
    }
}
.demo-btn{

    font-family: 'Kumbh Sans', sans-serif;
    font-style: normal;
    text-align: center;
    color: #4730D2;
    font-size: 36px;
    line-height: 1;
    font-weight: bold;
    margin: 0 auto;
    display: block;
    margin-top: 105px;

    &--undefined{
        background-color: #fff;
        color: inherit;
        width: 360px;
        border-radius: 16px;
        box-shadow: 0px 16px 36px rgba(31, 21, 91, 0.36);
        text-decoration: none;
        cursor: pointer;
        display: block;
        padding: 21px 0px 19px 0px;
        margin: 0 auto;
        @include media ("<=smallTablet"){
            width: 90%;
            max-width: 380px;
        }
    }
    &--defined{
        background-color: #15B743;
        color: #fff;
        width: 360px;
        border-radius: 16px;
        box-shadow: 0px 16px 36px rgba(31, 21, 91, 0.36);
        text-decoration: none;
        display: block;
        padding: 21px 0px 19px 0px;
        font-size: 28px;
        margin: 0 auto;
        @include media ("<=smallTablet"){
            width: 90%;
            max-width: 380px;
        }
    }
    @include media ("<=smallTablet"){
        margin-top: 50px;
    }
}
.section__sub{
    padding-top: 140px;
    padding-bottom: 20px;
}
.two-column{
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: space-between;
    margin: 20px 20px 0px 20px;
    &--reflected{
        .two-column__img{
            order: 1;
        }
        p{
            text-align: right;
        }
        .two-column__info{
            padding-left: 0px;
            padding-right: 45px;
            order: 2;
        }
        .index{
            left: auto;
            right: 0;
        }
        @include media ("<=smallTablet"){
            p{
                text-align: left;
            }
            .two-column__info{
                padding-right: 0px;
            }
            .index{
                left: 0;
                right: auto;
            }
        }
    }
    @include media ("<=smallTablet"){
        flex-direction: column;
        margin: 20px 0px 0px 0px;
    }
}
.two-column__info{
    padding-left: 45px;
    max-width: 505px;
    position: relative;
    @include media ("<=smallTablet"){
        padding-left: 0;
        padding-top: 25px;
        order: 2;
        max-width: 100%;
        margin-top: 20px;
        margin-bottom: 30px;
    }
}
.two-column__info-title{
    font-family: 'Kumbh Sans', sans-serif;
    font-style: normal;
    text-align: left;
    color: #1F155B;
    font-size: 36px;
    line-height: 1.2;
    font-weight: bold;
    margin: 0;
    @include media ("<=smallTablet"){
        font-size: 32px;
    }
}
.two-column__info-text{
    font-family: 'Kumbh Sans', sans-serif;
    font-style: normal;
    text-align: left;
    color: #6F6C82;
    font-size: 24px;
    line-height: 1.3;
    font-weight: 400;
    margin: 0;
    margin-top: 15px;
}
.two-column__img{

    &--1{
        width: 348px;
        margin-right: 70px;
        margin-top: 10px;
    }
    &--2{
        width: 343px;
        margin-left: 65px;
    }
    &--3{
        width: 501px;
        margin-right: -50px;
    }
    &--4{
        width: 405px;
        margin-right: 25px;
        margin-top: 65px;
    }
    &--5{
        width: 460px;
        margin-left: 35px;
    }
    &--6{
        width: 429px;
        margin-right: 11px;
        margin-top: 82px;
    }
    &--7{
        width: 405px;
        margin-right: 10px;
        margin-top: 53px;
    }
    &--8{
        width: 465px;
        margin-left: 20px;
        margin-top: 25px;
    }
    &--9{
        width: 480px;
        margin-right: -11px;
        margin-top: 17px;
    }
    img{
        width: 100%;
        height: auto;
    }
    @include media ("<=smallTablet"){
        width: 100%;
        max-width: 336px;
        margin-left: 0;
        margin-right: 0;
        order: 1;
    }
}
.index{
    font-family: 'Kumbh Sans', sans-serif;
    font-style: normal;
    text-align: left;
    font-size: 12px;
    line-height: 1.2;
    font-weight: bold;
    position: absolute;
    left: 0;
    top: 20px;
    &--purple{
        color: #6D59E4;
    }
    &--green{
        color: #47CE7D;
    }
    &--blue{
        color: #7BAAF1;
    }
    @include media ("<=smallTablet"){
        top: 0;
    }
}
.sub-nav{
    position: fixed;
    bottom: 30px;
    right: 0;
    left: 0;
    width: 236px;
    height: 100px;
    border-radius: 60px;
    background-color: #fff;
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: space-between;
    padding: 9px 32px;
    margin: 0 auto;
    z-index: 10;
    box-shadow: 0px 4px 6px 0px rgba(0,0,0,0.25);

    opacity: 0;
    pointer-events: none;
    transition: opacity 0.3s ease;

    &--active {
        opacity: 1;
        pointer-events: all;
        transition: opacity 0.3s ease;
    }
}

.sub-nav__icon-img-1{
    width: 38px;
    height: auto;
    position: absolute;
    top: 22px;
    left: 6px;
}
.sub-nav__icon-img-2{
    width: 38px;
    height: auto;
    position: absolute;
    top: 22px;
    left: 6px;
    opacity: 0;
}
.sub-nav__icon-img-3{
    width: 42px;
    height: 21px;
    position: absolute;
    top: -3px;
    left: -9px;
    right: 0;
    opacity: 0;
    display: block;
    margin: 0 auto;
}
.sub-nav__icon-img-4{
    width: 42px;
    height: 21px;
    position: absolute;
    bottom: -3px;
    left: -9px;
    right: 0;
    opacity: 0;
    display: block;
    margin: 0 auto;
}
.sub-nav__icon{
    width: 68px;
    height: 100%;
    position: relative;
    &--2{
        .sub-nav__icon-img-1{
            width: 38px;
            top: 21px;
            left: 12px;
        }
        .sub-nav__icon-img-2{
            width: 38px;
            top: 21px;
            left: 12px;
        }
        .sub-nav__icon-img-3{
            left: 2px;
        }
        .sub-nav__icon-img-4{
            left: 2px;
        }
    }
    &--3{
        .sub-nav__icon-img-1{
            width: 36px;
            top: 22px;
            left: 19px;
        }
        .sub-nav__icon-img-2{
            width: 36px;
            top: 22px;
            left: 19px;
        }
        .sub-nav__icon-img-3{
            left: 15px;
        }
        .sub-nav__icon-img-4{
            left: 15px;
        }
    }
    &.active{
        .sub-nav__icon-img-2{
           opacity: 1;
